import React, { useEffect, useRef } from "react";
import L from "leaflet";

interface TroopPosition {
  lat: number;
  lng: number;
  quantity: number;
}

interface Props {
  center: [number, number];
  troops: TroopPosition[];
}

export const MapView: React.FC<Props> = ({ center, troops }) => {
  const mapContainer = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (!mapContainer.current) return;

    const map = L.map(mapContainer.current).setView(center, 4);
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "&copy; OpenStreetMap contributors",
    }).addTo(map);

    troops.forEach((t) => {
      L.circle([t.lat, t.lng], {
        radius: t.quantity * 1000,
      }).addTo(map);
    });

    // Fix for resizing issues
    setTimeout(() => map.invalidateSize(), 0);

    return () => {
      map.remove();
    };
  }, [center, troops]);

  return <div ref={mapContainer} className="w-full h-96 rounded" />;
};
