import React from "react";
import { CountrySelect } from "../components/CountrySelect";
import { useFetch } from "../hooks/useFetch";
import { fetchCountries } from "../api";
import { useWar } from "../context/WarContext";
import { Loader } from "../components/Loader";

export const Home: React.FC = () => {
  const { data: countries, loading } = useFetch(fetchCountries);
  const { config, setConfig } = useWar();

  if (loading) return (
    <div className="flex items-center justify-center min-h-screen">
      <Loader />
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-4xl mx-auto bg-white rounded-xl shadow-md overflow-hidden p-6">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">War Simulation Configurator</h1>
          <p className="text-gray-600">Select two countries to simulate a military conflict</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="space-y-4">
            <h2 className="text-xl font-semibold text-gray-700">Country A</h2>
            <CountrySelect
              countries={countries || []}
              selected={config.countryA}
              onChange={(code) => setConfig((c) => ({ ...c, countryA: code }))}
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            />
          </div>

          <div className="space-y-4">
            <h2 className="text-xl font-semibold text-gray-700">Country B</h2>
            <CountrySelect
              countries={countries || []}
              selected={config.countryB}
              onChange={(code) => setConfig((c) => ({ ...c, countryB: code }))}
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            />
          </div>
        </div>

        <div className="mt-8 flex justify-center">
          <button
            onClick={() => console.log('Start simulation')}
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white font-medium rounded-lg shadow-md transition duration-200"
            disabled={!config.countryA || !config.countryB}
          >
            Start Simulation
          </button>
        </div>
      </div>
    </div>
  );
};
