import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchResults } from "../api";
import { Loader } from "../components/Loader";
import { MapView } from "../components/MapView";

interface Result {
  simulationId: string;
  winner: string;
  probability: number;
  troopPositions: { lat: number; lng: number; quantity: number }[];
}

export const Results: React.FC = () => {
  const { simulationId } = useParams<{ simulationId: string }>();
  const [result, setResult] = useState<Result | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchResults(simulationId!)
      .then((res) => setResult(res.data))
      .finally(() => setLoading(false));
  }, [simulationId]);

  if (loading || !result) return <Loader />;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Simulation Results</h1>
      <p className="mb-2">
        Winner: <strong>{result.winner}</strong>
      </p>
      <p className="mb-4">
        Win Probability: <strong>{result.probability}%</strong>
      </p>
      <MapView center={[20, 0]} troops={result.troopPositions} />
    </div>
  );
};
