import React from "react";
import { CountrySelect } from "../components/CountrySelect";
import { useFetch } from "../hooks/useFetch";
import { fetchCountries } from "../api";
import { useWar } from "../context/WarContext";
import { Loader } from "../components/Loader";

export const Home: React.FC = () => {
  const { data: countries, loading } = useFetch(fetchCountries);
  const { config, setConfig } = useWar();

  // Inline styles object
  const styles = {
    container: {
      minHeight: '100vh',
      padding: '1.5rem',
      backgroundColor: '#f8fafc'
    },
    header: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '1rem'
    },
    selectWrapper: {
      display: 'flex',
      gap: '1rem',
      marginTop: '2rem'
    }
  };

  if (loading) return <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
    <Loader />
  </div>;

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Configure Your War</h1>
      
      <div style={styles.selectWrapper}>
        <div style={{ flex: 1 }}>
          <CountrySelect
            countries={countries || []}
            selected={config.countryA}
            onChange={(code) => setConfig((c) => ({ ...c, countryA: code }))}
          />
        </div>
        
        <div style={{ flex: 1 }}>
          <CountrySelect
            countries={countries || []}
            selected={config.countryB}
            onChange={(code) => setConfig((c) => ({ ...c, countryB: code }))}
          />
        </div>
      </div>
    </div>
  );
};
